const files = msg.files;
const groups = {};

for (let i = 0; i < files.length; i++) {
    const match = files[i].match(/([^\/]+)_T1/);
    const sampleId = match[1];
    if (!groups[sampleId]) groups[sampleId] = [];
    groups[sampleId].push(files[i]);
}

const lines = ["sample,fastq_1,fastq_2,strandedness"];
for (const sample in groups) {
    const files = groups[sample];
    const fastq_1 = files.find(f => f.includes("_1.fastq.gz"));
    const fastq_2 = files.find(f => f.includes("_2.fastq.gz"));
    lines.push(`${sample},${fastq_1},${fastq_2},reverse`);
}

msg.payload = lines.join("\n");
return msg;
